mapper.insert:
INSERT INTO product (
category_seq
, user_seq
, product_name
, product_price
, product_status
, product_amount
, product_weight )
VALUES (
#{categorySeq}
, #{userSeq}
, #{productName}
, #{productPrice}
, #{productStatus}
, #{productAmount}
, #{productWeight} )

mapper.update:
UPDATE product
SET product_name = #{ productName }
, product_price = #{ productPrice }
, product_amount = #{ productAmount }
, product_weight = #{ productWeight }
WHERE product_seq = #{ productSeq }
AND user_seq = #{ userSeq }
